import React, { useState } from "react";
import "./App.scss";
import { Login, Register } from "./components/login/index";
import UserInfo from "./components/userInfo/UserInfo";
import { useNavigate } from 'react-router-dom';

const App = () => {
  const [isLogginActive, setIsLogginActive] = useState(true);
  const navigate = useNavigate();

  const changeState = () => {
    setIsLogginActive(prevState => !prevState);
  };

  const navigateToDetails = (section) => {
    navigate(`/details/${section}`);
  };

  if (document.cookie.includes('authToken')) {
    return <UserInfo navigateToDetails={navigateToDetails} />;
  }

  const current = isLogginActive ? "Register" : "Login";
  const currentActive = isLogginActive ? "login" : "register";

  return (
    <div className="App">
      <div className="login">
        <div className="container">
          {isLogginActive ? <Login /> : <Register />}
        </div>
        <RightSide
          current={current}
          currentActive={currentActive}
          onClick={changeState}
        />
      </div>
    </div>
  );
};

const RightSide = props => {
  return (
    <div className="right-side" onClick={props.onClick}>
      <div className="inner-container">
        <div className="text">{props.current}</div>
      </div>
    </div>
  );
};

export default App;
