import React from "react";
import "./App.scss";
import { Login, Register } from "./components/login/index";
import UserInfo from "./components/userInfo/UserInfo";
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLogginActive: true
    };
  }

  componentDidMount() {
    //Add .right by default
    if (this.container) {
    this.rightSide.classList.add("right");
    }
  }

  changeState() {
    const { isLogginActive } = this.state;

    if (isLogginActive) {
      this.rightSide.classList.remove("right");
      this.rightSide.classList.add("left");
    } else {
      this.rightSide.classList.remove("left");
      this.rightSide.classList.add("right");
    }
    this.setState(prevState => ({ isLogginActive: !prevState.isLogginActive }));
  }

  navigateToDetails = (section) => {
    // Implement your navigation logic here
    // For example, you can use React Router to navigate to different tabs
    // You can also update the state to control which tab is active
    console.log("Navigating to details section:", section);
  };


  navigateToDetails = (section) => {
    // Implement your navigation logic here
    // For example, you can use React Router to navigate to different tabs
    // You can also update the state to control which tab is active
    console.log("Navigating to details section:", section);
    // Update the state to control which tab is active
    this.setState({ activeTab: section });
  };

  render() {
    if (document.cookie.includes('authToken')) {
      return <UserInfo navigateToDetails={this.navigateToDetails}/>;
    }

    const { isLogginActive, activeTab } = this.state;
    const current = isLogginActive ? "Register" : "Login";
    const currentActive = isLogginActive ? "login" : "register";
    return (
      <div className="App">
        <div className="login">
          <div className="container" ref={ref => (this.container = ref)}>
            {isLogginActive && (
              <Login containerRef={ref => (this.current = ref)} />
            )}
            {!isLogginActive && (
              <Register containerRef={ref => (this.current = ref)} />
            )}
          </div>
          <RightSide
            current={current}
            currentActive={currentActive}
            containerRef={ref => (this.rightSide = ref)}
            onClick={this.changeState.bind(this)}
          />
        </div>
        <Tabs selectedIndex={activeTab} onSelect={index => this.setState({ activeTab: index })}>
          <TabList>
            <Tab>Title 1</Tab>
            <Tab>Title 2</Tab>
            <Tab>Title 3</Tab>
          </TabList>
          <TabPanel>
            <h2>Content 1</h2>
          </TabPanel>
          <TabPanel>
            <h2>Content 2</h2>
          </TabPanel>
          <TabPanel>
            <h2>Content 3</h2>
          </TabPanel>
        </Tabs>
      </div>
    );
  }
}

const RightSide = props => {
  return (
    <div
      className="right-side"
      ref={props.containerRef}
      onClick={props.onClick}
    >
      <div className="inner-container">
        <div className="text">{props.current}</div>
      </div>
    </div>
  );
};

export default App;
